#include <iostream>
#include <math.h>
#include <string>

using namespace std;
string calc(long n, long k) {
  if (n == 0)
    return "0";

  if (n % int(pow(-2, k + 1)) == 0)
    return calc(n, k + 1) + "0";
  return calc(n - pow(-2, k), k + 1) + "1";
}

int main() {
  int N;
  cin >> N;
  string ans = calc(N, 0);
  if (ans.size() != 1)
    ans.erase(ans.begin());
  cout << ans << endl;
}

