#include <algorithm>
#include <iostream>
#include <sstream>
using namespace std;

int main() {
  int n;
  cin >> n;
  string ans;
  stringstream ss;
  stringstream rs;
  for (; true;) {
    int rank;
    int a;
    a = abs(n);
    rank = a % 2;
    n = (n - rank) / (-2);
    ss << rank;
    if (n == 1 or n == -1) {
      break;
    }
  }
  if (n == 1) {
    ss << 1;
  } else {
    ss << 11;
  }
  ss >> ans;
  reverse(ans.begin(), ans.end());
  cout << ans << "\n";
}

