#include <algorithm>
#include <bitset>
#include <cmath>
#include <cstdint>
#include <iomanip>
#include <iostream>
#include <queue>
#include <random>
#include <stack>
#include <string>
#include <utility>
#include <vector>
using namespace std;
using ll = long long;

int main() {
  string s;
  int n, memo = 2, count = 0;
  cin >> n;
  if (n == 0) {
    cout << 0 << endl;
    return 0;
  }
  while (n) {
    if (abs(n) % memo != 0) {
      s.insert(0, "1");
      n -= ((count % 2 == 0) ? (memo / 2) : (-1 * (memo / 2)));
    } else {
      s.insert(0, "0");
    }
    memo *= 2;
    count++;
  }
  cout << s << endl;
  return 0;
}

