#include <bits/stdc++.h>
#define rep(i, n) for (int i = 0; i < (n); ++i)
#define all(v) v.begin(), v.end()
using namespace std;
typedef long long ll;

int main() {

  int n;
  cin >> n;

  char ans[100];
  int digit;
  if (n == 0) {
    puts("0");
  } else if (n > 0) {
    // positive
    digit = 1;
    ll max_num = 1;
    ll min_num;
    while (max_num < n) {
      min_num = max_num;
      digit += 2;
      max_num += 1LL << (digit - 1);
    }
    ans[0] = '1';
    for (int i = 1; i < digit; i++) {
      int mid = (max_num + min_num) / 2;
      if (n <= mid) {
        ans[i] = (i % 2 == 0) ? '0' : '1';
        max_num = mid;
      } else {
        ans[i] = (i % 2 == 0) ? '1' : '0';
        min_num = mid;
      }
    }
  } else {
    // negative
    n *= -1;
    digit = 2;
    ll max_num = 2;
    ll min_num;
    while (max_num < n) {
      min_num = max_num;
      digit += 2;
      max_num += 1LL << (digit - 1);
    }
    ans[0] = '1';
    for (int i = 1; i < digit; i++) {
      int mid = (max_num + min_num) / 2;
      if (n <= mid) {
        ans[i] = (i % 2 == 0) ? '0' : '1';
        max_num = mid;
      } else {
        ans[i] = (i % 2 == 0) ? '1' : '0';
        min_num = mid;
      }
    }
  }

  rep(i, digit) { printf("%c", ans[i]); }
  cout << endl;
  return 0;
}

