int MAX_INDEX = 40;

string create(long long val, vector<long long> &max_table,
              vector<long long> &min_table, vector<long long> &pow_table) {
  if (val == 0) {
    return "0";
  }

  int index = 0;

  for (; index < MAX_INDEX; ++index) {
