  int base = -2;
  while (abs(N) > 0) {
    if (check(N + base, base * (-2))) { // trueなら
      v.push_back(1);
      N -= base;
    } else {
      v.push_back(0);
    }
    base *= (-2);
  }
  reverse(v.begin(), v.end());
  for (auto e : v)
    cout << e;
  cout << endl;
}
int main() {
  cin >> N;
  solve();
  return 0;
}

