    if (x % 2 == 0) {
      s.push_back('0');
      x /= (-2);
    } else {
      s.push_back('1');
      x = (x - 1) / (-2);
    }
  }
  for (auto i = s.end() - 1; i >= s.begin(); i--) {
    cout << (*i);
  }

  return 0;
}

