  int ans = 100000;
  for (int i = 0; i < (1 << D); i++) {
    int tmpCnt = 0, tmpScore = 0;
    for (int j = 0; j < D; j++) {
      if ((1 << j) & i) {
        tmpCnt += p[j];
        tmpScore += p[j] * (j + 1) * 100 + c[j];
      }
