        int plusCnt =
            min(p[j], (G - tmpScore + problemScore - 1) / problemScore);
        tmpScore += plusCnt * problemScore;
        tmpCnt += plusCnt;
      }
    }
    ans = min(ans, tmpCnt);
  }
  cout << ans << endl;
}

