  int D, G;
  cin >> D >> G;
  vector<int> p = vector<int>(D);
  vector<int> c = vector<int>(D);
  int result = 0;
  for (int i = 1; i <= D; i++) {
    cin >> p[i] >> c[i];
    result += p[i];
    c[i] /= 100;
  }

  // unordered_map<int, int> memo;
  map<int, int> memo;
  G /= 100;
  memo[0] = 0;
  for (int i = 1; i <= D; i++) {
    map<int, int> temp_memo;
    temp_memo = memo;
    for (auto it = temp_memo.begin(); it != temp_memo.end(); it++) {
      int source = it->first;
      int source_val = it->second;
      for (int j = 1; j <= p[i]; j++) {
        // int prev = source + i * (j - 1);
        if (memo.find(source + i * j) == memo.end()) {
          memo[source + i * j] = source_val + j;
        } else if (memo[source + i * j] > source_val + j) {
          memo[source + i * j] = source_val + j;
        }
      }

      int prev = source + i * p[i];
      if (memo.find(prev + c[i]) == memo.end()) {
        memo[prev + c[i]] = source_val + p[i];
      } else if (memo[prev + c[i]] > memo[source] + p[i]) {
        memo[prev + c[i]] = source_val + p[i];
      }
    }
  }

