void func(int sum, int res, int index, long flag) {
  if (sum >= G) {
    ans = min(ans, res);
    return;
  }
  if (index == D) {
    int fi = D - 1;
    while (G > sum) {
      if (!(flag & (1 << fi))) {
        for (int j = 0; j < p[fi]; j++) {
          sum += (fi + 1) * 100;
          res++;
          if (sum >= G)
            break;
        }
        flag ^= (1 << (fi));
      }
      fi--;
    }
    ans = min(ans, res);
    return;
  }
  flag ^= (1 << index);
  func(m[index].first + sum, m[index].second + res, index + 1, flag);
  flag ^= (1 << index);
  func(sum, res, index + 1, flag);
  return;
}

int main() {
  cin >> D >> G;
