//
// Created by Huang,Zhiqiang on 2018/8/13.
//

#include <algorithm>
#include <iostream>
using namespace std;

const int INF = int(1e9);
int n, goal;
int con[15][2];
int dp[15][int(1e5)];

int DP(int x, int y) {
  if (y <= 0)
    return 0;
  if (x < 1)
    return INF;
  if (dp[x][y] != -1)
    return dp[x][y];
  dp[x][y] = INF;
  for (int i = 0; i < con[x][0]; i++)
    dp[x][y] = min(dp[x][y], DP(x - 1, y - i * x) + i);
  dp[x][y] =
      min(dp[x][y], DP(x - 1, y - con[x][0] * x - con[x][1]) + con[x][0]);
  return dp[x][y];
}

int main() {
  for (int i = 0; i < 15; i++) {
    for (int j = 0; j < 1e5; i++) {
      dp[i][j] = -1;
    }
  }
  cin >> n >> goal;
  goal /= 100;
  for (int i = 1; i <= n; i++) {
    cin >> con[i][0] >> con[i][1];
    con[i][1] /= 100;
  }
  cout << DP(n, goal) << endl;
}

