int res;

int cache[12][550005];

int foo(int now, int score) {
  if (now >= d) {
    if (score >= g)
      return 0;
    return 1e8;
  }
  if (score >= g)
    return 0;
  int &ret = cache[now][score];
  if (~ret)
    return ret;
  ret = 1e9;

  ret = foo(now + 1, score);

  for (int i = 0; i < p[now] - 1; i++) {
    int x = foo(now + 1, score + (i + 1) * (now + 1) * 100);
    ret = min(ret, x + i + 1);
  }

  ret = min(ret,
            p[now] + foo(now + 1, score + p[now] * (now + 1) * 100 + c[now]));
  return ret;
}

int main() {
  cin >> d >> g;
  for (int i = 0; i < d; ++i) {
    scanf("%d %d", &p[i], &c[i]);
  }

  memset(cache, -1, sizeof cache);
  res = 1e9;
  res = foo(0, 0);
  cout << res << endl;
}

