  scanf("%d%d", &d, &g);
  for (i = 0; i < d; i++)
    scanf("%d%d", &p[i], &c[i]);
  int ans = INF;
  for (i = 0; i < (1 << d); i++) {
    ll sum = 0;
    int ss = 0;
    for (j = 0; j < d; j++) {
      if ((i >> j) & 1) {
        sum += p[j] * (j + 1) * 100 + c[i];
        ss += p[j];
      }
    }
    for (j = d - 1; j >= 0; j--) {
      if ((i >> j) & 1)
        continue;
      if (sum >= g)
