      int s;
      s = (g - sum) / ((j + 1) * 100);
      s = min(s, p[j]);
      ss += s;
      sum += (j + 1) * 100 * s;
    }
    if (g <= sum)
      ans = min(ans, ss);
  }
  printf("%d\n", ans);
  return 0;
}

