using namespace std;

int a[111], b[111];

int d, g;

int dp[11][2 * 1111111];
int check(int i, int sum) {

  if (sum <= 0)
    return 0;
  if (i > d)
    return 10000;
  if (~dp[i][sum])
    return dp[i][sum];
  int res = INT_MAX, r;

  for (int j = 0; j <= a[i]; j++) {
    r = 100 * j * i;
    if (j == a[i])
      r += b[i];

    res = min(res, j + check(i + 1, sum - r));
  }
  return dp[i][sum] = res;
}

