  vector<vector<int>> v(D, vector<int>(3));
  for (int i = 0; i < D; i++) {
    cin >> v.at(i).at(0) >> v.at(i).at(1);
    v.at(i).at(2) = 100 * (i + 1) * v.at(i).at(0) + v.at(i).at(1);
  }
  int ans = 1000;
  for (int bit = 0; bit < (1 << D); bit++) {
    int sum = 0, count = 0, rest_max = -1;
    for (int i = 0; i < D; i++) {
      if (bit & (1 << i)) {
        sum += v.at(i).at(2);
        count += v.at(i).at(0);
      } else {
        rest_max = i;
      }
      if (sum < G) {
        int s = 100 * (rest_max + 1);
        int need = (G - sum + s - 1) / s;
        if (need < v.at(i).at(0)) {
          count += need;
          break;
        }
      }
    }
    ans = min(ans, count);
  }
  cout << ans << endl;
}

