#include <algorithm>
#include <iostream>
#include <vector>
using namespace std;
template <class T> inline bool chmax(T &a, T b) {
  if (a < b) {
    a = b;
    return 1;
  }
  return 0;
}

typedef long long ll;

int main() {
  freopen("input.txt", "r", stdin);

  int D, G;
  cin >> D >> G;
  ll problems[D];
  ll bonuses[D];

  for (int i = 0; i < D; i++) {
    cin >> problems[i];
    cin >> bonuses[i];
  }

  // まずどの問題のボーナスポイントをとるか決める
  // 2^D通りある
  ll ans = 1 << 29;
  for (int bit = 0; bit < (1 << D); bit++) {
    ll sum = 0, num = 0;
    for (int i = 0; i < D; i++) {
      // bitがたっていたら全部解く
      if (bit & (1 << i)) {
        sum += problems[i] * 100 * (i + 1) + bonuses[i];
        num += problems[i];
      }
    }

    if (sum >= G) {
      ans = min(ans, num);
    } else {
      // まだ目標に届いていない場合は貪欲に
      for (int i = D - 1; i >= 0; i--) {
        if (bit & (1 << i))
          continue;
        for (int j = 0; j < problems[i]; j++) {
          if (sum >= G) {
            break;
          }
          sum += (i + 1) * 100;
          num++;
        }
      }

      ans = min(ans, num);
    }
  }

  cout << ans;
  return 0;
}

