#include <bits/stdc++.h>
using namespace std;
using uint = unsigned int;
using ll = long long;
using ull = unsigned long long;

#define FOR(i, a, b) for (int i = a; i < b; ++i)
#define FORR(i, a, b) for (int i = b - 1; i >= a; --i)
#define REP(i, n) FOR(i, 0, n)
#define REPR(i, n) FORR(i, 0, n)

template <class T> inline void read(T &x) {
  T s = 0, base = 1;
  char c;
  while (!isdigit(c = getchar()))
    if (c == '-')
      base = -base;
  while (isdigit(c)) {
    s = s * 10 + (c ^ 48);
    c = getchar();
  }
  x = base * s;
}

int D, G;
int p[10], c[10];

int main() {
  read(D);
  read(G);
  G /= 100;

  REP(i, D) {
    read(p[i]);
    read(c[i]);
    c[i] /= 100;
  }

  int ans = numeric_limits<int>::max();
  for (int i = 0; i < (1 << D); ++i) {
    // i: 使いきったか否かを表すビット
    int sum = 0;
    int cnt = 0;

    for (int j = 0; j < D; ++D) {
      if ((i >> j) & 1) {
        sum += p[j] * (j + 1) + c[j];
        cnt += p[j];
      }
    }

    for (int j = D - 1; j >= 0; --j) {
      if ((i >> j) & 1)
        continue;
      if (sum >= G)
        break;
      int buf = min((G - sum) / (j + 1), p[j]);
      cnt += buf;
      sum += (j + 1) * buf;
    }

    if (G <= sum)
      ans = min(ans, cnt);
  }

  printf("%d\n", ans);
  return 0;
}

