#include <iostream>
using namespace std;

typedef struct {
  int q, c;
} Problem;

typedef long long ll;
const int N = 11;
Problem problems[N];
const int S = 1e7;
int dp[N][S];
bool used[N][S];
int d, g;
const int inf = 1e9;

int solve(int i, int s) {
  if (s >= g) {
    return 0LL;
  }

  if (i >= d) {
    return inf;
  }

  int &ans = dp[i][s];

  if (used[i][s]) {
    return ans;
  }

  ans = inf;

  for (int k = 0; k <= problems[i].q; k++) {
    int add = 0;

    if (k == problems[i].q) {
      add = problems[i].c;
    }

    ans = min(ans, k + solve(i + 1, (i + 1) * k + add + s));
  }

  return ans;
}

int main() {

  cin >> d >> g;

  g /= 100;

  for (int i = 0; i < d; i++) {
    cin >> problems[i].q >> problems[i].c;
    problems[i].c /= 100;
  }

  cout << solve(0, 0) << endl;

  return 0;
}

