#include <iostream>
#include <vector>
using namespace std;

constexpr int INF = 1e9;
constexpr int MAX_SCORE = 101010;
int score_cnt[MAX_SCORE];

int main() {
  int d, g;
  cin >> d >> g;
  g /= 100;

  for (int i = 0; i < MAX_SCORE; i++) {
    score_cnt[i] = INF;
  }

  score_cnt[0] = 0;

  for (int i = 1; i <= d; i++) {
    int p, c;
    cin >> p >> c;
    c /= 100;
    // すでに解かれてる問題＋その問題
    for (int j = MAX_SCORE - 1; j >= 0; j--) {
      if (score_cnt[j] != INF) {
        for (int k = 1; k <= p; k++) {
          if (j + i * k < MAX_SCORE) {
            score_cnt[j + i * k] = min(score_cnt[j + i * k], score_cnt[j] + k);
          }
        }
        if (j + i * p < MAX_SCORE) {
          score_cnt[j + i * p + c] =
              min(score_cnt[j + i * p + c], score_cnt[j] + p);
        }
      }
    }

    // その問題だけ
    for (int j = 1; j <= p; j++) {
      score_cnt[i * j] = min(score_cnt[i * j], j);
    }
    // 全完した場合
    score_cnt[i * p + c] = min(score_cnt[i * p + c], p);
  }

  int ans = 1e9;
  for (int i = g; i < MAX_SCORE; i++) {
    ans = min(ans, score_cnt[i]);
  }
  cout << ans << endl;
}

