#define FOR(i, b, e) for (int i = (b); i < (e); ++i)
#define FORQ(i, b, e) for (int i = (b); i <= (e); ++i)
#define FORD(i, b, e) for (int i = (b)-1; i >= (e); --i)
#define REP(x, n) for (int x = 0; x < (n); ++x)

#define ST first
#define ND second
#define PB push_back
#define MP make_pair
#define LL long long
#define ULL unsigned LL
#define LD long double

const double pi =
    3.141592653589793238462643383279502884197169399375105820974944592307816406286208998628034825342;
const int mod = 1000000007;

// mod演算ライブラリ
// mod=1000000007

LL add(LL a, LL b) { return (a + b) % mod; }

LL sub(LL a, LL b) { return (a + mod - b) % mod; }

LL mul(LL a, LL b) { return ((a % mod) * (b % mod)) % mod; }

LL power(LL x, LL y) { // 繰り返し二乗法 単品利用可
  if (y == 0)
    return 1;
  else if (y == 1)
    return x % mod;
  else if (y % 2 == 0) {
    LL tmp = power(x, y / 2) % mod;
    return tmp * tmp % mod;
  } else {
    LL tmp = power(x, y / 2) % mod;
    return ((tmp * tmp) % mod) * x % mod;
  }
}

LL dvs(LL a, LL b) { return mul(a, power(b, mod - 2)); }

const int SIZE_mod = 200005;
int invfact[SIZE_mod];
int fact[SIZE_mod];

LL factset(int a) { // aまでの階乗%mod表を作成
  fact[0] = 1;
  FORQ(i, 1, a) { fact[i] = mul(fact[i - 1], i); }
}

LL cmbset(int a) { // 1~aまでの階乗と階乗数の逆元のmod(mod)を用意 O(a)
  factset(a);
  invfact[a] = power(fact[a], mod - 2);
  invfact[0] = 1;
  FORD(i, a, 1) { invfact[i] = mul(invfact[i + 1], i + 1); }
}

LL cmb(int n, int r) { // nCr%mod  (cmbset後に使用)
  if (n < r || r < 0 || n < 0)
    return 0;
  return mul(fact[n], mul(invfact[r], invfact[n - r]));
}

LL prm(int n, int r) { // nPr%mod
  return mul(cmb(n, r), fact[n]);
}

LL homo(int n, int r) { // nHr%mod
  return cmb(n + r - 1, r);
}

int main() {
  string s;
  cin >> s;
  LL dp[101010][3] = {};
  int l = s.length();
  int tmp = 1LL;
  FOR(i, 1, l + 1) {
    if (s[i - 1] == 'A') {
      dp[i][0] = add(dp[i - 1][0], tmp);
      dp[i][1] = dp[i - 1][1];
      dp[i][2] = dp[i - 1][2];
    } else if (s[i - 1] == 'B') {
      dp[i][1] = add(dp[i - 1][0], dp[i - 1][1]);
      dp[i][0] = dp[i - 1][0];
      dp[i][2] = dp[i - 1][2];

    } else if (s[i - 1] == 'C') {
      dp[i][2] = add(dp[i - 1][1], dp[i - 1][2]);
      dp[i][1] = dp[i - 1][1];
      dp[i][0] = dp[i - 1][0];

    } else {
      dp[i][0] = add(3LL * dp[i - 1][0], tmp);
      dp[i][1] = add(dp[i - 1][0], 3LL * dp[i - 1][1]);
      dp[i][2] = add(dp[i - 1][1], 3LL * dp[i - 1][2]);
      tmp = mul(tmp, 3LL);
    }
  }
  cout << dp[l][2];
  return 0;
}

