  ios::sync_with_stdio(false);
  cin.tie(nullptr);
  cout.tie(nullptr);

  int three[100005];
  three[0] = 1;
  REP(i, 100001) { three[i + 1] = (three[i] * 3) % mod; }

  string S;
  cin >> S;
  int n = S.size();
  vector<int> A(n);
  vector<int> C(n);
