        ans += ((la * rq) % mod * three[sumq - 1]) % mod;
      if (sumq >= 1)
        ans += ((rc * lq) % mod * three[sumq - 1]) % mod;
      if (sumq >= 2)
        ans += ((lq * rq) % mod * three[sumq - 2]) % mod;
