      if (sumq >= 2)
        ans += ((rc * lq) % mod * three[sumq - 2]) % mod;
      if (sumq >= 3)
        ans += ((lq * rq) % mod * three[sumq - 3]) % mod;
    }
  }
  cout << ans % mod << endl;

  return 0;
}

