#include <iostream>
#include <math.h>
#include <string>
#include <vector>

using namespace std;

int mod_pow(int x, int y, int MOD, vector<int> &vc) {
  long long ans = 1;
  for (int i = 0; i < y; i++) {
    ans *= x;
    ans %= MOD;
    vc[i + 1] = ans;
  }
  return ans;
}

int main() {
  string s;
  int const MOD = pow(10, 9) + 7;
  long long A = 0;
  long long AB = 0;
  long long ABC = 0;
  int cnt = 0;
  int num = 0;
  cin >> s;

  for (int i = 0; i < s.size(); i++) {
    char x = s[i];
    if (x == '?') {
      num++;
    }
  }

  vector<int> mPow(num, 1);
  mod_pow(3, num, MOD, mPow);

  for (int i = 0; i < s.size(); i++) {
    char x = s[i];
    if (x == 'A') {
      A += 1 * mPow[cnt];
    } else if (x == 'B') {
      AB += A;
    } else if (x == 'C') {
      ABC += AB;
    } else {
      ABC *= 3;
      ABC += AB;
      AB *= 3;
      AB += A;
      A *= 3;
      A += mPow[cnt];
      cnt++;
    }
    A %= MOD;
    AB %= MOD;
    ABC %= MOD;
  }
  cout << ABC << '\n';
}

