    return next == 'D' ? 1 : 0;
  if (s[pos] == '?') {
    long consume = next != 'D' ? dp(pos + 1, next + 1) : 0;
    return (3 * dp(pos + 1, next) + consume) % MOD;
  } else {
    long consume = s[pos] == next ? dp(pos + 1, next + 1) : 0;
    return (dp(pos + 1, next) + consume) % MOD;
  }
