      X[i + 1] += X[i] + p(cnt - 1);
      Y[i + 1] += Y[i];
      Z[i + 1] += Z[i];
    }
    if (flag == 2 || flag == 4) {
      X[i + 1] += X[i];
      Y[i + 1] += Y[i] + X[i];
      Z[i + 1] += Z[i];
