  string s;
  cin >> s;
  int len = s.size();
  long long dp[100002][3] = {{0}}; // 欲しいのは dp[0][0]
  long long cntHatena[100001] = {0}, po[100001] = {0};

  for (int i = len; i >= 0; i--) {
    cntHatena[i] = ((s[i] == '?') ? 1 : 0) + cntHatena[i + 1];
    po[i] = pow3(cntHatena[i]);
  }
  char al[3] = {'A', 'B', 'C'};

  for (int i = len - 1; i >= 0; i--) {
    if (s[i] == 'C') {
      dp[i][2] = dp[i + 1][2] + po[i + 1];
    } else if (s[i] == '?') {
      dp[i][2] = 3 * dp[i + 1][2] + po[i + 1];
    } else {
      dp[i][2] = dp[i + 1][2];
    }
    dp[i][2] %= P;
  }

  for (int j = 1; j >= 0; j--) {
    for (int i = len - 1; i >= 0; i--) {
      if (s[i] == al[j]) {
