#include <bits/stdc++.h>
using namespace std;

constexpr long mod = 1000000007;

long powpow(long x, long y) {
  long r = 1;
  while (y) {
    if (y & 1)
      r *= x, r %= mod;
    x *= x, x %= mod;
    y >>= 1;
  }
  return r;
}

int main(void) {
  string s;
  cin >> s;
  int l = s.size();

  vector<vector<long>> dp(3, vector<long>(4));

  long q = 0;
  for (size_t i = 0; i < l; i++)
    q += s[i] == '?';

  for (size_t i = 0; i < l; i++) {
    for (int j : {2, 1, 0})
      for (int k : {2, 1, 0}) {
        dp[j][k] += (j > 0 ? dp[j - 1][k] : k == 0) * (s[i] == 'A' + j),
            dp[j][k] %= mod;
        dp[j][k + 1] += (j > 0 ? dp[j - 1][k] : k == 0) * (s[i] == '?'),
            dp[j][k + 1] %= mod;
      }
  }

  long r = 0;
  for (int i : {0, 1, 2, 3})
    r += dp[2][i] * powpow(3, q - i), r %= mod;

  cout << r << endl;
}

