#include <bits/stdc++.h>
using namespace std;

// 与2数の絶対値の差を返す
int absolute_diff(int a, int b) {
  if (a - b >= 0)
    return a - b;
  else
    return b - a;
}

// 与パターンのコストの和を返す
int p_sum(int a, int b, int c) {
  return absolute_diff(a, b) + absolute_diff(b, c);
}

int main() {
  int A, B, C;
  cin >> A >> B >> C;
  int m;
  m = min(p_sum(A, B, C), p_sum(A, C, B));
