  string S, T;
  cin >> S, T;

  bool CAN = true;
  for (int i = 0; i < S.size(); ++i) {
    CAN = true;
    for (int j = 0; j < S.size(); ++j) {
      if (j + i < S.size()) {
        if (S.at(j + i) != T.at(j))
          CAN = false;
      } else {
        if (S.at(j + i - S.size()) != T.at(j))
          CAN = false;
      }
    }
    if (CAN)
      break;
  }

  if (CAN)
