/*
考虑一个很显然的O(nlogn)的做法
首先对所有线段按照右端点排序，然后按照右端点切

由于所有线段的值域为1 - n，我们可以对所有左端点，直接记录出右端点为位置
再扫一遍即可
*/
#include <algorithm>
#include <cstdio>
using namespace std;
const int MAXN = 2001;
inline int read() {
  char c = getchar();
  int x = 0, f = 1;
  while (c < '0' || c > '9') {
    if (c == '-')
      f = -1;
    c = getchar();
  }
  while (c >= '0' && c <= '9')
    x = x * 10 + c - '0', c = getchar();
  return x * f;
}
int N, M;
int r[MAXN];
int main() {
  N = read();
  M = read();
  for (int i = 1; i <= N; i++)
    r[i] = N + 1;
  for (int i = 1; i <= M; i++) {
    int x = read(), y = read();
    r[x] = min(y, r[x]);
  }
  int cur = N + 1, ans = 0;
  for (int i = 1; i <= N; i++) {
    if (r[i] != N + 1)
      cur = min(cur, r[i]);
    if (i == cur)
      cur = min(N + 1, r[i]), ans++;
  }
  printf("%d", ans);
  return 0;
}
/*

*/

