#include <algorithm>
#include <queue>
#include <stdio.h>
#include <string.h>
using namespace std;
#define N 100010
#define inf 1000000007
typedef struct {
  int u, v, next;
} Node;
Node e[N * 3];
int head[N], vis[N], d[N];
int tot, l, r;
void add(int x, int y, int z) {
  e[tot].u = y;
  e[tot].v = z;
  e[tot].next = head[x];
  head[x] = tot++;
}
void Spfa() {
  int i, j, k, t;
  queue<int> q;
  memset(vis, 0, sizeof(vis));
  for (i = l; i <= r; i++)
    d[i] = -inf;
  d[l] = 0;
  vis[l] = 1;
  q.push(l);
  while (!q.empty()) {
    t = q.front();
    q.pop();
    vis[t] = 0;
    for (i = head[t]; i != -1; i = e[i].next) {
      j = e[i].u;
      k = e[i].v;
      if (d[j] < d[t] + k) {
        d[j] = d[t] + k;
        if (vis[j] == 0) {
          vis[j] = 1;
          q.push(j);
        }
      }
    }
  }
}
int main() {
  int i, j, k, x, y, z, n, m;
  scanf("%d%d", &n, &m);
  memset(head, -1, sizeof(head));
  tot = 0;
  l = inf;
  r = -inf;
  for (i = 0; i < m; i++) {
    scanf("%d%d", &x, &y);
    x = x * 2 + 1;
    y = y * 2 - 1;
    add(x, y + 1, 1);
    l = min(l, x);
    r = max(r, y + 1);
  }
  for (i = l; i < r; i++) {
    add(i, i + 1, 0);
    add(i + 1, i, -1);
  }
  Spfa();
  printf("%d\n", d[r]);
  return 0;
}

