#define MAXM 100010

struct Edge {
  int next, to, w;
} edge[MAXM];
int head[MAXN], cnt;

void Add(int u, int v, int w) {
  edge[++cnt].w = w;
  edge[cnt].next = head[u];
  head[u] = cnt;
  edge[cnt].to = v;
}
// 上面是链式前向星存图
int N, M;
int dis[MAXN];
bool vis[MAXN];

void SPFA(int S) { // SPFA模版
  queue<int> Q;
  Q.push(S);
  for (int i = 0; i <= N + 1; i++)
    dis[i] = 1e9;
  dis[S] = 0;
  vis[S] = 1;
  while (!Q.empty()) {
    int k = Q.front();
    Q.pop();
    vis[k] = 0;
    for (int i = head[k]; i != -1; i = edge[i].next) {
      int to = edge[i].to;
      if (dis[to] > dis[k] + edge[i].w) {
        dis[to] = dis[k] + edge[i].w;
        if (!vis[to]) {
          Q.push(to);
          vis[to] = 1;
        }
      }
    }
  }
}

int main() {
  int x, y, t, S;
  cin >> N >> M;
  S = N + 1; // 源点为N+1
  memset(head, -1, sizeof(head));
  for (int i = 0; i <= N; i++)
    Add(S, i, 0); // Sum(i) - Sum(N+1) <= 0
  for (int i = 1; i <= M; i++) {
    cin >> x >> y;
    Add(y, x, -1); // Sum(x-1) - Sum(y) <= -t
  }
  for (int i = 1; i <= N; i++) {
