    Add(i, i - 1, 0); // Sum(i-1) - Sum(i) <= 0
  }
  SPFA(S);
  int _min = 1e9;
  for (int i = 0; i <= N; i++)
    _min = min(_min, dis[i]);
  cout << dis[N] - _min << endl;
  return 0;
}

