void init(int n_) {
  n = 1;
  while (n < n_)
    n *= 2;
  for (int i = 0; i < 2 * n - 1; i++) {
    dat[i] = 0;
    dat2[i] = 0;
  }
}

void initset(int k, int a) {
  k += n - 1;
  dat[k] = a;
  while (k > 0) {
    k = (k - 1) / 2;
    dat[k] = dat[k * 2 + 1] + dat[k * 2 + 2];
  }
}

//[a,b)の間を[l,r]区間で比較しアップデート
// 引数のindexに注意
// nは固定。initで計算すみ
// update2(L[i],R[i]+1,0,0,n,D[i]);
void update2(int a, int b, int k, int l, int r,
             int v) { // v更新値、区間は0-index
  if (r <= a || b <= l)
    return;
  if (a <= l && r <= b) {
    dat[k] += dat2[k];
    if (r - l > 1) {
      dat2[k * 2 + 1] += dat2[k] / 2;
      dat2[k * 2 + 1] += dat2[k] / 2;
    }
    dat2[k] = v * (r - l);
    return;
  } else {
    update2(a, b, k * 2 + 1, l, (l + r) / 2, v);
    update2(a, b, k * 2 + 2, (l + r) / 2, r, v);
    return;
  }
}

int query(int a, int b, int k, int l, int r) {
  if (r <= a || b <= l)
    return 0;
  if (a <= l && r <= b) {
    dat[k] += dat2[k];
    if (r - l > 1) {
      dat2[k * 2 + 1] += dat2[k] / 2;
      dat2[k * 2 + 1] += dat2[k] / 2;
    }
    dat2[k] = 0;
    return dat[k];
  } else {
    int vl = query(a, b, k * 2 + 1, l, (l + r) / 2);
    int vr = query(a, b, k * 2 + 2, (l + r) / 2, r);
    return vl + vr;
  }
}

