using namespace std;

typedef long long ll;
typedef pair<ll, ll> P;

#define MAX_N 110000

template <typename T> class SegTree {
public:
  int n;
  T dat[2 * MAX_N - 1];

  void init(int n_, T init_num) {
    n = 1;
    while (n < n_)
      n *= 2;
    for (int i = 0; i < 2 * n - 1; i++) {
      dat[i] = init_num;
    }
  }

  // k番目の値(0-indexed)をaに変更
  void update(int k, T a) {
    k += n - 1;
    dat[k] = a;
    while (k > 0) {
      k = (k - 1) / 2;
      dat[k] = min(dat[k * 2 + 1], dat[k * 2 + 2]);
    }
  }

  // [a, b)の最小値を求める。
