  bool b = 1;
  if (a <= 1)
    return 0;
  else {
    rep1(i, sqrt(a) - 1) {
      if (a % (i + 1) == 0)
        b = 0;
    }
    return b;
  }
}
int modpow(int a, int b, int m = MOD) {
  if (!b)
    return 1;
  if (b & 1)
    return modpow(a, b - 1, m) * a % m;
  int memo = modpow(a, b >> 1, m);
  return memo * memo % m;
}
int fact_mod(int n) {
  int f = 1;
  for (int i = 2; i < n + 1; i++)
    f = f * (i % MOD) % MOD;
  return f;
}
int mod_pow(int x, int n) {
  int res = 1;
  while (n > 0) {
    if (n & 1)
      res = (res * x) % MOD;
    x = (x * x) % MOD;
    n >>= 1;
  }
  return res;
}
int c_mod(int n, int r) {
  if (r > n - r)
    r = n - r;
  if (r == 0)
    return 1;
  int a = 1;
  rep(i, r) a = a * ((n - i) % MOD) % MOD;
  int b = mod_pow(fact_mod(r), MOD - 2);
  return (a % MOD) * (b % MOD) % MOD;
}
