template <typename T> inline T read() {
  register T sum = 0, fg = 1;
  register char ch(getchar());
  for (; !isdigit(ch); ch = getchar())
    if (ch == '-')
      fg = -1;
  for (; isdigit(ch); ch = getchar())
    sum = (sum << 3) + (sum << 1) - '0' + ch;
  return sum * fg;
}

