    bool flag = true;
    bool subflag = false;
    long long k = A[i] % B[i];
    long long x, y;
    long long g = GCD(B[i], D[i]);
    if (B[i] > A[i])
      flag = false;
    if (B[i] > D[i])
      flag = false;
    if (flag == true && C[i] >= B[i]) {
      subflag = true;
    }
    if (flag == true && subflag == false) {
      for (long long j = 0; j < B[i]; j++) {
        if ((A[i] + g * j) % B[i] > C[i])
