#include <iostream>
#include <numeric>

using namespace std;
using ll = long long;
using ld = long double;

ll gcd(ll m, ll n) {
  // 引数に０がある場合は０を返す
  if ((0 == m) || (0 == n))
    return 0;

  // ユークリッドの方法
  while (m != n) {
    if (m > n)
      m = m - n;
    else
      n = n - m;
  }
  return m;
}

bool isMugen(ll A, ll B, ll C, ll D) {
  if (A < B) {
    return false;
  }

  if (D < B) {
    return false;
  }
  if (C >= B) {
    return true;
  }

  ll E = A % B;
  ll F = D % B;

  if (C < E) {
    return false;
  }
  if (F == 0) {
    return true;
  }

  ll H = gcd(B, F);
  if (B - H > C) {
    return false;
  }

  return true;
}

