#include <bits/stdc++.h>
#define ll long long
using namespace std;
#define mx 22
int MOD = 902014037;

ll pw[mx];
int prm = 103;
int n;
char str[mx * 2];

bool isprime(int n) {
  for (int i = 2; 1ll * i * i <= n; i++)
    if (n % i == 0)
      return 0;
  return 1;
}

map<pair<int, int>, int> mp, mp2;

void rec(int pos, int hash1, int len1, int hash2) {
  if (pos == n) {
    mp[{hash1, hash2}]++;
    return;
  }
  int tmp = hash1;
  hash1 = hash1 + pw[len1] * str[pos] % MOD;
  hash1 %= MOD;
  rec(pos + 1, hash1, len1 + 1, hash2);
  hash2 = (1ll * hash2 * prm + str[pos]) % MOD;
  rec(pos + 1, tmp, len1, hash2);
}

void rec2(int pos, int hash1, int len1, int hash2) {
  if (pos == n * 2) {
    mp2[{hash2, hash1}]++;
    return;
  }
  int tmp = hash1;
  hash1 = hash1 + pw[len1] * str[pos] % MOD;
  hash1 %= MOD;
  rec2(pos + 1, hash1, len1 + 1, hash2);
  hash2 = (1ll * hash2 * prm + str[pos]) % MOD;
  rec2(pos + 1, tmp, len1, hash2);
}

int main() {
  for (int i = 8; i >= 0; i--)
    while (isprime(MOD) == 0)
      MOD--;

  scanf("%d", &n);
  scanf("%s", str);

  pw[0] = 1;
  for (int i = 1; i < mx; i++) {
    pw[i] = pw[i - 1] * prm % MOD;
  }
  rec(0, 0, 0, 0);
  rec2(n, 0, 0, 0);
  ll ans = 0;
  for (auto p : mp) {
    ans += 1ll * mp2[p.first] * p.second;
  }
  printf("%lld\n", ans);

  return 0;
}

