int tot;
void insert(string tmp) {
  //	cout << tmp << endl;
  int rt = 0;
  for (int i = 0; i < tmp.length(); ++i) {
    int tar = tmp[i] - 'a';
    if (nx[rt][tar] == -1) {
      nx[rt][tar] = ++tot;
      sum[tot] = 0;
    }
    rt = nx[rt][tar];
  }
  sum[rt]++;
}
int search(string tmp) {
  //	cout << "hh" << tmp << endl;
  int rt = 0;
  for (int i = 0; i < tmp.length(); ++i) {
    int tar = tmp[i] - 'a';
    if (nx[rt][tar] == -1) {
      return 0;
    }
    rt = nx[rt][tar];
  }
  return sum[rt];
}

int main() {
  int n;
  while (~scanf("%d", &n)) {
    memset(nx, -1, sizeof(nx));
    sum[0] = 0;
    tot = 0;
    memset(has, 0, sizeof(has));

    scanf("%s", seq);
    for (int i = 0; i < n; ++i) {
      has[seq[i] - 'a']++;
    }
    bool flag = true;
    for (int i = n; i < 2 * n && flag; ++i) {
      has[seq[i] - 'a']--;
      if (has[seq[i] - 'a'] == -1) {
        flag = false;
      }
    }
    if (!flag) {
      printf("0\n");
      continue;
    }

