      for (int j = 0; j < n; ++j) {
        if ((i >> j) & 1) {
          tmp1 += seq[j];
        } else
          tmp2 += seq[j];
      }
      tmp1 += 'z' + 1;
      tmp1 += tmp2;
      insert(tmp1);
    }

    ll ans = 0;
    for (int i = 0; i < largeNum; ++i) {
      string tmp1, tmp2;
      for (int j = 0; j < n; ++j) {
        if ((i >> j) & 1) {
          tmp1 += seq[j + n];
        } else
          tmp2 += seq[j + n];
      }
      reverse(tmp1.begin(), tmp1.end());
      reverse(tmp2.begin(), tmp2.end());
      tmp1 += 'z' + 1;
      tmp1 += tmp2;

      ans += search(tmp1);
    }
    printf("%lld\n", ans);
  }
  return 0;
}

