LL powe(LL a, LL b) {
  LL r = 1;
  while (b) {
    if (b & 1) {
      (r *= a) %= MOD;
    }
    a = a * a % MOD;
    b >>= 1;
  }
  return r;
}

int H[105];
// ababababab..., any
pair<LL, LL> go(int a, int b, int offset) {
  // printf("%d %d %d\n", a, b, offset);

  if (a == b)
    return {1, 1};

  int mini = 1e9;
  FOR(i, a, b) mini = min(mini, H[i] - offset);
  // assert(mini > 0);

  if (a + 1 == b)
    return {powe(2, mini - 1), powe(2, mini)};

  int S = a;

  LL aba = 1, any = 1, aba_counted = 1;
  int minis = 0;
  FOR(i, a, b + 1) {
    if (i == b || H[i] - offset == mini) {
      if (i > S) {
        auto rec = go(S, i, offset + mini);

        (aba *= 2 * rec.st) %= MOD;
        (any *= (rec.nd + 2 * rec.st)) %= MOD;
      }

      S = i + 1;

      if (i < b) {
