    for (int j = min(a[i], a[i - 1]); j; --j)
      f[i][j] = (LL)f[i - 1][j] * coef % MOD;

    for (int j = a[i - 1] + 1; j <= a[i]; ++j) {
      coef =
          (j == 1 ? fpm(2, val[j]) - 2 : fpm(2, val[j] - val[j - 1] + 1) - 2) %
