  if (n % 2 == 0)
    return printf("%d %d\n", max(sum1, sum2), min(sum1, sum2)), 0;
  int ans, l = -1.5e9, r = 1.5e9;
  while (l <= r) {
    int mid = l + r >> 1;
    if (check(mid))
      l = mid + 1, ans = mid;
    else
      r = mid - 1;
  }
  return printf("%d %d\n", sum1 + ans, sum2 - ans), 0;
}

