  gpsn.dijkstra(t);

  vector<ll> res(n);
  rep(i, n) { res[i] = gpen.get_d(s, i) + gpsn.get_d(t, i); }

  ll M = 1e+15;
  ll tmp = M;
  vector<ll> ans(n);
  for (int i = n - 1; i >= 0; --i) {
    tmp = min(tmp, res[i]);
    ans[i] = M - tmp;
  }

  rep(i, n) cout << ans[i] << "\n";

  return 0;
}

