    dqy.adj_[u].push_back(make_pair(v, a));
    dqy.adj_[v].push_back(make_pair(u, a));
    dqs.adj_[u].push_back(make_pair(v, b));
    dqs.adj_[v].push_back(make_pair(u, b));
  }
  dqy.Compute(S);
  dqs.Compute(T);
  ull minv = MAXCOST;
